import React from "react";
import "../../../node_modules/bootstrap/dist/css/bootstrap.min.css";

const Player = (props) => {
  const { id, name, salary, image } = props.item;
  // console.log([props.item]);

  const removePlayer = (num) => {
    const remaining = [props.item];
    console.log(remaining);
    const newPeople = remaining.filter((player) => player.id !== num);
    console.log(newPeople);
  };
  return (
    <div
      key={id}
      className="card"
      style={{ width: "16rem", borderRadius: "10px", margin: "10px" }}
    >
      <img
        src={image}
        style={{ width: "100%", borderRadius: "5px", height: "200px" }}
        className="card-img-top"
        alt={name}
      />
      <div className="card-body text-center ">
        <h4 className="card-title">{name}</h4>
        <h5 className="card-text">Salary: â‚¬ {salary}</h5>
        <button
          onClick={() => props.playerHandler(props.item)}
          className="btn btn-info mt-3"
        >
          Add To List
        </button>
        <button onClick={() => removePlayer(id)} className="btn btn-danger m-3">
          Remove
        </button>
      </div>
    </div>
  );
};

export default Player;
