import React from "react";
import "../../../node_modules/bootstrap/dist/css/bootstrap.min.css";

const Player = (props) => {
  const { id, name, country, salary, image } = props.item;
  return (
    <div
      class="card"
      style={{ width: "15rem", borderRadius: "10px", margin: "10px" }}
    >
      <img
        src={image}
        style={{ width: "150px", borderRadius: "10px", height: "200px" }}
        class="card-img-top"
        alt={name}
      />
      <div class="card-body">
        <h5 class="card-title">Name: {name}</h5>
        <p class="card-text">Salary: {salary}</p>
        <button
          onClick={() => props.playerHandler(props.name)}
          class="btn btn-primary"
        >
          Add
        </button>
      </div>
    </div>
  );
};

export default Player;
