import React from "react";
import "../../../node_modules/bootstrap/dist/css/bootstrap.min.css";

const Player = (props) => {
  const { id, name, salary, image } = props.item;
  return (
    <div key={id} className="card" style={{ width: "16rem", margin: "10px" }}>
      <img
        src={image}
        style={{ width: "100%", borderRadius: "10px", height: "200px" }}
        className="card-img-top"
        alt={name}
      />
      <div className="card-body">
        <h3 className="card-title">Name: {name}</h3>
        <h5 className="card-text">Salary: â‚¬ {salary}</h5>
        <button
          onClick={() => props.playerHandler(props.item)}
          className="btn btn-info"
        >
          Add To List
        </button>
      </div>
    </div>
  );
};

export default Player;
